var models = require('config/models');
var user = models.user;

exports.user_profile = function(user_target_name, callback){
	user.find({username : user_target_name}, function(err, users){
		if(users.length != 0){
			var avatar = users[0].avatar;
			var cover_image = users[0].cover_image;
			callback({'response' : 'access success', 'avatar' : avatar, 'cover_image' : cover_image});
		}
	})
}